    public void expired(long block) throws Exception
    {
        // Load  markets
        UTILS.DB.executeUpdate("DELETE "
                               + "FROM assets_mkts  "
                              + "WHERE expires<="+block+" "
                                + "AND expires>0");
    }
    
    public void reorganize(long block, String chk_hash) throws Exception
    {
       // Load checkpoint
       this.loadCheckpoint(chk_hash);
    }
   
